const swiper = new Swiper('.swiper-container', {
    // オプション
    loop: true,
    slidesPerView: 1,
    spaceBetween: 0,
    pagination: {
      el: '.swiper-pagination',
      clickable: true,
    },
    navigation: {
      nextEl: '.swiper-button-next',
      prevEl: '.swiper-button-prev',
    },
    on: {
      slideChangeTransitionEnd: function () {
        const activeSlide = this.slides[this.activeIndex];
        const topMessage = activeSlide.querySelector('.swiper-message-top p');
        const bottomMessage = activeSlide.querySelector('.swiper-message-bottom p');
  
        // フェードインのアニメーション
        if (topMessage) {
          topMessage.style.opacity = 1;
          topMessage.style.transition = 'opacity 0.5s ease-in-out';
        }
        if (bottomMessage) {
          bottomMessage.style.opacity = 1;
          bottomMessage.style.transition = 'opacity 0.5s ease-in-out';
        }
      },
      slideChangeTransitionStart: function () {
        const activeSlide = this.slides[this.activeIndex];
        const topMessage = activeSlide.querySelector('.swiper-message-top p');
        const bottomMessage = activeSlide.querySelector('.swiper-message-bottom p');
  
        // スライドが変わる前にフェードアウト（必要であれば）
        if (topMessage) {
          topMessage.style.opacity = 0;
          topMessage.style.transition = 'opacity 0.3s ease-in-out';
        }
        if (bottomMessage) {
          bottomMessage.style.opacity = 0;
          bottomMessage.style.transition = 'opacity 0.3s ease-in-out';
        }
      },
    },
  });